import React from 'react';
import {Link} from 'react-router-dom';
import { useParams } from "react-router-dom";

// Layout

// Import Images




import { useStateValue } from '../../StateProvider';


// Import Images
import bnrImg1 from "../../images/banner/img1.jpg";
import bg2 from "../../images/main-banner/bg2.jpg"
import ServiceContainer from './serviceContainer';




const Services = () => {
	const [{ nightMode }, dispatch] = useStateValue();
	let params = useParams();
	let { service } = params;
	return (
		<>

		<div className="page-content bg-white">

			<div className="banner-wraper">
				<div className="page-banner" style={nightMode ? {backgroundImage: "url("+bg2+")"} : {backgroundImage: "url("+bnrImg1+")"}}>
					<div className="container">
						<div className="page-banner-entry text-center">
							<h1>Services</h1>
							<nav aria-label="breadcrumb" className="breadcrumb-row">
								<ul className="breadcrumb">
									<li className="breadcrumb-item"><Link to="/"><svg xmlns="http://www.w3.org/2000/svg" width="22" height="22" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" className="feather feather-home"><path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path><polyline points="9 22 9 12 15 12 15 22"></polyline></svg> Home</Link></li>
									<li className="breadcrumb-item active" aria-current="page">Services</li>
									<li className="breadcrumb-item active" aria-current="page">{service}</li>
								</ul>
							</nav>
						</div>
					</div>

				</div>
			</div>

		</div>

<ServiceContainer/>

	</>
	);
};

export default Services;
